import pandas as pd
from typing import Union, List, Set, Dict

class OrthoGroupReader:
    """读取OrthoFinder的Orthogroups.tsv结果文件
    
    Attributes:
        file_path (str): Orthogroups.tsv文件路径
        sp1_genes (set): 物种1的所有基因集合
        sp2_genes (set): 物种2的所有基因集合
        sp1_to_sp2 (dict): 物种1到物种2的基因映射字典
        sp2_to_sp1 (dict): 物种2到物种1的基因映射字典
    """
    
    def __init__(self, file_path, strip_gene_name=True):
        self.file_path = file_path
        self.sp1_genes = set()  # 物种1的所有基因
        self.sp2_genes = set()  # 物种2的所有基因
        self.sp1_to_sp2 = {}    # 物种1到物种2的映射
        self.sp2_to_sp1 = {}    # 物种2到物种1的映射
        self.skip_count = 0
        self.strip_gene_name = strip_gene_name
        self._parse_file()
        
    def _parse_file(self):
        """使用pandas解析Orthogroups.tsv文件"""
        # 读取tsv文件,跳过注释行
        df = pd.read_csv(self.file_path, sep='\t', comment='#')
        
        # 遍历每一行建立映射关系
        for _, row in df.iterrows():
            # 处理物种1的基因
            sp1_genes = []
            if pd.notna(row[1]):  # 检查是否为NA
                sp1_genes = [g.strip() for g in row[1].split(',')]
                self.sp1_genes.update(sp1_genes)
            
            # 处理物种2的基因
            sp2_genes = []
            if pd.notna(row[2]):  # 检查是否为NA
                sp2_genes = [g.strip() for g in row[2].split(',')]
                self.sp2_genes.update(sp2_genes)
            
            # 建立映射关系
            if sp1_genes and sp2_genes:
                for g1 in sp1_genes:
                    if g1 not in self.sp1_to_sp2:
                        self.sp1_to_sp2[g1] = set()
                    self.sp1_to_sp2[g1].update(sp2_genes)
                    
                for g2 in sp2_genes:
                    if g2 not in self.sp2_to_sp1:
                        self.sp2_to_sp1[g2] = set()
                    self.sp2_to_sp1[g2].update(sp1_genes)
    
    def get_orthologs(self, gene_ids: Union[str, List[str]], from_sp1: bool = True) -> Union[Set[str], Dict[str, Set[str]]]:
        """获取指定基因的同源基因
        
        Args:
            gene_ids: 单个基因ID或基因ID列表
            from_sp1: 是否从物种1查找到物种2，默认True
            
        Returns:
            如果输入单个基因ID，返回同源基因集合
            如果输入基因ID列表，返回字典 {gene_id: 同源基因集合}
        """
        if isinstance(gene_ids, str):
            return self.sp1_to_sp2.get(gene_ids, set()) if from_sp1 else self.sp2_to_sp1.get(gene_ids, set())
        
        orthologs_dict = {}
        mapping = self.sp1_to_sp2 if from_sp1 else self.sp2_to_sp1
        for gene_id in gene_ids:
            orthologs_dict[gene_id] = mapping.get(gene_id, set())
        return orthologs_dict
    
    def get_stats(self):
        """获取基本统计信息
        
        Returns:
            dict: 包含基本统计信息的字典
        """
        return {
            'sp1_gene_count': len(self.sp1_genes),
            'sp2_gene_count': len(self.sp2_genes),
            'sp1_mapped_genes': len(self.sp1_to_sp2),
            'sp2_mapped_genes': len(self.sp2_to_sp1)
        }
